import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../static/Chat.css";
import Pusher from "pusher-js";

function Chat() {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const token = sessionStorage.getItem("token");
  const navigate = useNavigate();
  const url = "http://127.0.0.1:8000/edu/chatRoom";

  useEffect(() => {

    Pusher.logToConsole = true;

    const pusher = new Pusher("c4d1ea6264a50a99b57d", {
      cluster: "ap1",
    });

    const channel = pusher.subscribe("chatroom");
    channel.bind("message", function (data) {
      setMessages((prevMessages) => [...prevMessages, data]); // Update state immutably
    });

    return () => {
      channel.unbind_all();
      channel.unsubscribe();
    };
  }, [token, navigate]); // Add dependencies to prevent continuous execution

  const submit = async (e) => {
    e.preventDefault();
    await fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json", "X-Token": token },
      body: JSON.stringify({
        message,
      }),
    });
    setMessage(""); // Clear input field after sending message
  };

  return (
    <div className="container">
      <div className="messageView">
        {messages.map((message, index) => (
          <p key={index}>
            {message.user_name}: {message.message}
          </p>
        ))}
      </div>
      <div className="sendMessage">
        <form onSubmit={(e) => submit(e)}>
          <input
            type="text"
            placeholder="write a message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button type="submit">Send message</button>
        </form>
      </div>
    </div>
  );
}

export default Chat;
