import React, { useEffect, useState } from "react";
import "../static/Chat.css";
import Pusher from "pusher-js";
import { Link } from "react-router-dom";


function Chat() {
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const token = sessionStorage.getItem("token");

  useEffect(() => {
    Pusher.logToConsole = true;

    const pusher = new Pusher("c4d1ea6264a50a99b57d", {
      cluster: "ap1",
    });

    const channel = pusher.subscribe("chat");
    channel.bind("message", function (data) {
      console.log("Received message:", data); // Debug log
      setMessages((prevMessages) => [...prevMessages, data]);
    });
    return () => {
      pusher.unsubscribe("chat");
    };
  }, []);

  const submit = async (e) => {
    e.preventDefault();

    if (!message.trim()) return; // Avoid sending empty messages

    await fetch("http://localhost:8000/edu/chatRoom", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Token": token,
      },
      body: JSON.stringify({ message }),
    });

    setMessage("");
  };

  useEffect(() => {
    console.log("Messages updated:", messages); // Debug log
  }, [messages]);

  return (
    <>
    <div>
      <header className="chatlogo">
        <h2>EDU HUB</h2>
      </header>
      <nav className="chatnavBar">
        <ul className="chatnavBarLinks">
          <li>
            <Link to="/dashboard">Dashboard</Link>
          </li>
          <li>
            <Link to="/projects">Projects</Link>
          </li>
          <li>
            <Link to="/peers">Students</Link>
          </li>
          <li>
            <Link to="/account">Account</Link>
          </li>
          <li>
            <Link to="/chat">Chat</Link>
          </li>
        </ul>
      </nav>
      <div className="container">
        <div className="d-flex flex-column align-items-stretch flex-shrink-0 bg-white">
          <div className="d-flex align-items-center flex-shrink-0 p-3 link-dark text-decoration-none border-bottom">
            Chat Room
          </div>
          <div className="list-group list-group-flush border-bottom scrollarea">
            {messages.map((msg, index) => (
              <div
                key={index}
                className="list-group-item list-group-item-action py-3 lh-tight"
              >
                <div className="d-flex w-100 align-items-center justify-content-between">
                  <p>{msg.username}</p>
                </div>
                <p>{msg.message}</p>
              </div>
            ))}
          </div>
        </div>
        <form onSubmit={submit}>
          <input
            className="form-control"
            placeholder="Write a message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
        </form>
      </div>
      </div>
    </>
  );
}

export default Chat;
